operations.body:
query autoSuggest($query: String!, $pageSize: Int!) {
  autoSuggest(query: $query, pageSize: $pageSize, suggesterType: GENERAL)
}

query cosmo_BookCategory($categoryCode: ID!, $page: Int!, $sortOrder: PortalSortOrder!, $filterSaleType: BookSaleType, $excludeBookSakuhinCode: String) {
  webfront_bookCategory(
    menuCode: $categoryCode
    page: $page
    pageSize: 30
    sortOrder: $sortOrder
    filterSaleType: $filterSaleType
    excludeBookSakuhinCode: $excludeBookSakuhinCode
  ) {
    pageInfo {
      ...PageInfo
      __typename
    }
    books {
      ...BookTitleCard
      __typename
    }
    __typename
  }
}

fragment BookCard on Book {
  code
  name
  mediaType {
    code
    __typename
  }
  thumbnail {
    standard
    __typename
  }
  publisher {
    code
    name
    __typename
  }
  credits {
    personCode
    penName
    __typename
  }
  publishedIn {
    name
    __typename
  }
  isPreorderable
  publicStartDateTime
  __typename
}

fragment BookTitleCard on BookSakuhin {
  sakuhinCode: code
  name
  rate
  freeBookNum
  isNew
  isSale
  book {
    ...BookCard
    __typename
  }
  minPrice
  hasMultiplePrices
  totalBookNum
  isUnextOriginal
  paymentBadgeList {
    id
    code
    __typename
  }
  bookViewCode
  featurePieceCode
  subgenreTagList {
    name
    __typename
  }
  recentLicenseExpiredDatetime
  isChapter
  hasUnreadChapter
  isBookSakuhinTicketAvailable
  isBookPlusTicketAvailable
  __typename
}

