}

query cosmo_BookFeatures($bookFeatureCode: ID!, $page: Int, $pageSize: Int, $tagCode: String) {
  webfront_bookFeatures(
    bookFeatureCode: $bookFeatureCode
    page: $page
    pageSize: $pageSize
    tagCode: $tagCode
  ) {
    pageInfo {
      ...PageInfo
      __typename
    }
    feature {
      code
      name
      typeCode
      comment
      displayEnd
      books {
        ...BookTitleCard
        __typename
      }
      __typename
    }
    __typename
  }
}

