query cosmo_allFreewordSearch($query: String!, $page: Int, $pageSize: Int, $videoSortOrder: PortalSortOrder!, $bookSortOrder: PortalSortOrder!) {
  webfront_videoFreewordSearch(
    query: $query
    page: $page
    pageSize: $pageSize
    sortOrder: $videoSortOrder
  ) {
    titles {
      ...TitleCard
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
  webfront_bookFreewordSearch(
    query: $query
    page: $page
    pageSize: $pageSize
    sortOrder: $bookSortOrder
  ) {
    books {
      ...BookTitleCard
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
  webfront_personFreewordSearch(query: $query, page: $page, pageSize: $pageSize) {
    people {
      personName
      personCode
      personNameCode
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
  liveFreewordSearch(query: $query, page: $page, pageSize: $pageSize) {
    liveList {
      ...LiveCard
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
}

