query cosmo_allPersonSearch($personCode: ID!, $personNameCode: ID!, $page: Int, $pageSize: Int, $mainGenreId: String, $videoSortOrder: PortalSortOrder!, $bookSortOrder: PortalSortOrder!) {
  webfront_searchVideo(
    personCode: $personCode
    personNameCode: $personNameCode
    page: $page
    pageSize: $pageSize
    mainGenreId: $mainGenreId
    sortOrder: $videoSortOrder
  ) {
    personName
    titles {
      ...TitleCard
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
  webfront_bookPerson(
    personCode: $personCode
    personNameCode: $personNameCode
    page: $page
    pageSize: $pageSize
    sortOrder: $bookSortOrder
  ) {
    name
    books {
      ...BookTitleCard
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
  personLives(personCode: $personCode, page: $page, pageSize: $pageSize) {
    liveList {
      ...LiveCard
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
}

