query cosmo_bookRecommendBlocks($genreId: ID!, $page: Int, $pageSize: Int, $titlesPerBlock: Int!) {
  bookRecommendBlocks: webfront_bookRecommendBlocks(
    genreId: $genreId
    page: $page
    pageSize: $pageSize
    booksPerBlock: $titlesPerBlock
  ) {
    pageInfo {
      ...PageInfo
      __typename
    }
    recommendBlocks {
      __typename
      ... on BookSpotlightBlock {
        type
        code
        spotlightList {
          name
          code
          linkTypeCode
          thumbnail {
            secondary
            __typename
          }
          sakuhin {
            name
            code
            __typename
          }
          feature {
            name
            code
            __typename
          }
          featureTemplate {
            name
            code
            __typename
          }
          __typename
        }
        __typename
      }
      ... on BookRankingBlock {
        type
        code
        trackingCode
        books {
          ...BookTitleCard
          __typename
        }
        rankings {
          rankDiff
          isNewRanking
          __typename
        }
        results
        targets {
          code
          displayName
          books {
            ...BookTitleCard
            __typename
          }
          rankings {
            rankDiff
            isNewRanking
            __typename
          }
          __typename
        }
        __typename
      }
      ... on BookFeatureBlock {
        type
        code
        trackingCode
        feature {
          name
          code
          comment
          typeCode
          displayEnd
          books {
            ...BookTitleCard
            __typename
          }
          results
          __typename
        }
        __typename
      }
      ... on BookTemplateBlock {
        type
        code
        trackingCode
        template {
          heading
          displayEnd
          code
          typeCode
          url
          lead
          name
          featureArtwork {
            thumbnail {
              standard
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      ... on BookOriginalBlock {
        type
        code
        results
        leadTitle
        books {
          ...OriginalBookTitleCard
          __typename
        }
        __typename
      }
      ... on OriginalComicBlock {
        type
        code
        results
        leadTitle
        books {
          ...OriginalBookTitleCard
          __typename
        }
        __typename
      }
      ... on BookUserRecommendedBlock {
        type
        code
        results
        books {
          ...BookTitleCard
          __typename
        }
        heading
        comment
        __typename
      }
      ... on BookBlock {
        type
        code
        books {
          ...BookTitleCard
          __typename
        }
        results
        __typename
      }
      ... on MagazineBlock {
        type
        code
        name
        results
        magazineGenreCode
        trackingCode
        books {
          ...BookTitleCard
          __typename
        }
        __typename
      }
    }
    __typename
  }
}

