query cosmo_videoProductList($titleCode: ID!, $episodeCode: ID!, $deviceType: String!) {
  webfront_title_stage(id: $titleCode) {
    id
    titleName
    episode(id: $episodeCode) {
      episodeName
      displayNo
      __typename
    }
    __typename
  }
  webfront_videoProductList(titleCode: $titleCode, episodeCode: $episodeCode) {
    ppvProducts {
      code
      name
      saleTypeCode
      discountRate
      displayButtonText
      displayName
      purchaseDescription
      displaySaleType
      displayValidityDurationText
      discountRate
      originalPrice
      price
      isSale
      publicEndDate
      svodAvailableFromText
      __typename
    }
    contractProducts {
      code
      name
      typeCode
      price
      displaySaleType
      displayButtonText
      ruleTitle
      ruleNote
      packDescription {
        url {
          browser
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
  webfront_pointBack {
    setting {
      percentage
      maxPercentage
      productList
      scheduleDate
      isRestrictedToPoint
      canIncreasePercentage
      __typename
    }
    point
    isAnnouncedIos
    hasVideoSubscription
    __typename
  }
  productsForVideoContent(deviceType: $deviceType, id: $episodeCode) {
    subscriptions {
      ... on UnextSubscriptionBundle {
        name
        subscriptionType
        __typename
      }
      __typename
    }
    __typename
  }
}

