query cosmo_videoRecommendBlocks($genreCode: ID!, $page: Int!, $pageSize: Int!, $numPerBlock: Int!) {
  videoRecommendBlocks(
    genreCode: $genreCode
    page: $page
    pageSize: $pageSize
    numberOfContentPerBlock: $numPerBlock
  ) {
    blocks {
      ... on VideoRecommendLiveBlock {
        __typename
        code
        type
        trackingCode
        block {
          ...LiveBlock
          __typename
        }
      }
      ... on VideoRecommendTitleBlock {
        __typename
        ...CurationBlock
      }
      ... on VideoRecommendFeatureBlock {
        __typename
        code
        type
        trackingCode
        block {
          ...FeatureBlock
          __typename
        }
      }
      __typename
    }
    pageInfo {
      ...PageInfo
      __typename
    }
    __typename
  }
}

